SELECT COUNT(*) > 0
FROM boardlike
WHERE board_no = #{boardNo} AND user_no = #{userNo}

SELECT COUNT(*) > 0
FROM favor
WHERE board_no = #{boardNo} AND user_no = #{userNo}

SELECT boardimage_no, board_no, boardimage_name, boardimage_default_name
FROM boardimage
WHERE board_no = #{boardNo}

SELECT boardimage_no, board_no, boardimage_name, boardimage_default_name
FROM boardimage
WHERE boardimage_no = #{fileNo}
